import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { FETCH_CHARACTERS_REQUEST } from '../actions/actions';

interface CharactersState {
  loading: boolean;
  data: any | null;
  error: any | null;
}

const initialCharactersState: CharactersState = {
  loading: false,
  data: null,
  error: null,
};

//import { SET_EMAIL_VALUE, SET_SCORE_VALUE,
  //SET_ASSERTIONS_VALUE, SET_GRAVATAR } from '../actions';


const charactersReducer = (state = initialCharactersState, action: PayloadAction<any>) => {
  switch (action.type) {
  case FETCH_CHARACTERS_REQUEST:
    return { ...state, loading: true};
  // case SET_SCORE_VALUE:
  //   return { ...state, score: action.payload };
  // case SET_ASSERTIONS_VALUE:
  //   return { ...state, assertions: action.payload };
  // case SET_GRAVATAR:
  //   return { ...state, gravatarEmail: action.payload };
  default:
    return state;
  }
};

export default charactersReducer;

